original.css:
/* From Uiverse.io by AndrewHeinke - Tags: button */
.space-btn {
  background: #FBFBFF;
  border: 2px solid #FF4F00;
  color: #363537;
  font-size: 1rem;
  font-weight: bold;
  line-height: 1;
  letter-spacing: 1px;
  padding: .5rem .75rem;
  font-family: monospace;
  border-radius: 4px;
  display: flex;
  align-items: center;
  box-shadow: 0 2px 1px rgba(0,0,0,0.09), 
              0 4px 2px rgba(0,0,0,0.09), 
              0 8px 4px rgba(0,0,0,0.09), 
              0 16px 8px rgba(0,0,0,0.09),
              0 32px 16px rgba(0,0,0,0.09);
  transition: .3s ease-in;
}

.space-btn:hover {
  background: #FF4F00;
  color: white;
}

.space-btn svg {
  width: 1em;
  height: 1em;
  color: currentColor;
  margin-right: .5rem;
}

