/* From Uiverse.io by AnshKaushal - Tags: button */
button {
  border: 1.5px solid black;
  padding: 10px;
  width: 100px;
  margin: 15px;
  color: #f5f0f0;
  background-color: #101014;
  box-shadow: 3px 3px 6px #101014;
  transition: transform 0.3s ease-in-out;
}

button:hover {
  background-color: #f5f0f0;
  color: #101014;
  transform: scale(1.2);
}

