/* From Uiverse.io by BHARGAVPATEL1244 - Tags: button */
.space-button {
  display: inline-block;
  background-color: #111;
  color: #fff;
  border: none;
  border-radius: 30px;
  padding: 12px 32px;
  font-size: 1.2em;
  font-weight: bold;
  text-transform: uppercase;
  letter-spacing: 2px;
  position: relative;
  overflow: hidden;
  cursor: pointer;
  transition: background-color 0.3s ease;
}

.space-button span {
  position: relative;
  z-index: 1;
}

.space-button svg {
  position: absolute;
  top: 50%;
  right: 12px;
  transform: translateY(-50%);
  width: 16px;
  height: 16px;
  fill: #fff;
  transition: transform 0.3s ease;
}

.space-button:hover {
  background-color: #f8f8f8;
  color: #111;
}

.space-button:hover svg {
  transform: translateX(4px);
}

.space-button:active {
  background-color: #f94b4c;
  color: #fff;
}

