/* From Uiverse.io by Bastiennnn  - Tags: simple, animation, button, message, svg */
.button {
  cursor: pointer;
  width: 200px;
  height: 60px;
  background-color: white;
  font-size: medium;
  font-weight: bold;
  box-shadow: 0 5px 10px rgb(122, 122, 122);
  border-radius: 10px;
  border: 2px solid gray;
  transition: all 0.7s cubic-bezier(0.4, 0, 0.2, 1);
  position: relative;
  display: flex;
  align-items: center;
  justify-content: center;
}

.button:hover {
  box-shadow: 0 5px 10px rgb(58, 58, 58);
  transform: translateY(-10px);
}

.button:active {
  transform: scale(0.95);
  box-shadow: 0px 2px 5px rgba(0, 0, 0, 0.2);
}

.svg {
  padding-right: 8px;
  fill: none;
  height: 25px;
  transition: transform 1s cubic-bezier(0.4, 0, 0.2, 1);
}

.button:hover .svg {
  animation: flyAround 6s cubic-bezier(0.4, 0, 0.2, 1) infinite;
}

@keyframes flyAround {
  0% {
    transform: translate(0, 0) rotate(0deg) scale(1);
  }
  50% {
    transform: translate(20px, -100px) rotate(-300deg) scale(1);
  }
  100% {
    transform: translate(0, 0) rotate(0deg) scale(1);
  }
}

