original.css:
/* From Uiverse.io by Dashrath-Sharma - Tags: button, hover, glow, code, border, box */
button {
  color: red;
  outline: none;
  border: none;
  font-size: 17px;
  padding: 1em 2em;
  position: relative;
  background: none;
  cursor: pointer;
}

button:active {
  transform: scale(.98);
  box-shadow: .1px .1px 2px red;
}

button span {
  position: absolute;
  background: red;
  transition: .5s;
  box-shadow: 1px 1px 20px red;
}

button span:nth-child(1) {
  top: 0;
  left: 0;
  width: 3px;
  height: 30%;
}

button:hover span:nth-child(1) {
  height: 100%;
}

button span:nth-child(2) {
  top: 0;
  left: 0;
  width: 15%;
  height: 3px;
}

button:hover span:nth-child(2) {
  width: 100%;
}

button span:nth-child(3) {
  bottom: 0;
  right: 0;
  width: 3px;
  height: 30%;
}

button:hover span:nth-child(3) {
  height: 100%;
}

button span:nth-child(4) {
  bottom: 0;
  right: 0;
  width: 15%;
  height: 3px;
}

button:hover span:nth-child(4) {
  width: 100%;
}

