/* From Uiverse.io by Fadhilmagass  - Tags: icon, animation, button */
.space-button {
  display: -webkit-inline-box;
  display: -ms-inline-flexbox;
  display: inline-flex;
  -webkit-box-align: center;
  -ms-flex-align: center;
  align-items: center;
  -webkit-box-pack: center;
  -ms-flex-pack: center;
  justify-content: center;
  padding: 1rem 1.5rem;
  font-size: 1.25rem;
  color: #fff;
  background: -webkit-gradient(linear, left top, right bottom, from(#0a192f), to(#1c3548));
  background: linear-gradient(to bottom right, #0a192f, #1c3548);
  border: none;
  border-radius: 0.5rem;
  cursor: pointer;
  position: relative;
  overflow: hidden;
}

.space-button:before {
  content: "";
  position: absolute;
  top: 50%;
  left: 50%;
  -webkit-transform: translate(-50%, -50%);
  -ms-transform: translate(-50%, -50%);
  transform: translate(-50%, -50%);
  width: 0;
  height: 0;
  background-color: rgba(255, 255, 255, 0.2);
  border-radius: 50%;
  -webkit-transition: width 0.3s ease, height 0.3s ease;
  transition: width 0.3s ease, height 0.3s ease;
}

.space-button:hover:before {
  width: 500px;
  height: 500px;
}

.space-button:hover .space-button-icon {
  -webkit-transform: rotate(360deg);
  -ms-transform: rotate(360deg);
  transform: rotate(360deg);
}

.space-button:hover {
  background: -webkit-gradient(linear, left top, right bottom, from(#0b5ed7), to(#1c3548));
  background: linear-gradient(to bottom right, #0b5ed7, #1c3548);
}

.space-button:active {
  background-color: #0642ab;
}

.space-button-text {
  margin-right: 0.5rem;
}

.space-button-icon {
  width: 1.25rem;
  height: 1.25rem;
  fill: currentColor;
  margin-right: 0.5rem;
}

