/* From Uiverse.io by Github-Suriya  - Tags: material design, white, button, smooth, message */
button {
  font-family: inherit;
  font-size: 20px;
  background: #eef0ee;
  padding: 0.3em;
  border: none;
  border-radius: 15px;
  overflow: hidden;
  transition: all 0.2s;
  cursor: pointer;
  box-shadow: 0px 0px 6px #c5c5c5;
}

button span {
  display: block;
  margin-left: 0.3em;
  font-weight: 600;
  transition: all 0.3s ease-in-out;
}

button svg {
  display: block;
  transform-origin: center center;
  transition: transform 0.3s ease-in-out;
}

button .svg-name-con {
  background: #fcfdfc;
  border-radius: 25px;
  display: flex;
  align-items: center;
  padding: 0.7em 1em;
}

button:hover .svg-wrapper {
  animation: fly-1 0.6s ease-in-out infinite alternate;
}

button:hover svg {
  transform: translateX(4.2em) rotate(45deg) scale(1.1);
}

button:hover span {
  transform: translateX(9em);
}

button:active {
  transform: scale(0.95);
}

@keyframes fly-1 {
  from {
    transform: translateY(0.1em);
  }

  to {
    transform: translateY(-0.1em);
  }
}

