/* From Uiverse.io by HecktorViegas  - Tags: button */
.button {
  cursor: pointer;
  display: flex;
  text-wrap: nowrap;
  align-items: center;
  gap: 0.9rem;
  padding: 1.5rem 2rem;
  border-radius: 1.2rem;
  background: linear-gradient(to bottom, #e3a8fc, #8d38cf);
  box-sizing: border-box;
  box-shadow:
    -3px 5px 1.5px #5a2982,
    -15px 12px 20px #5a2982ba;
  border: none;
  transform: rotate3d(2, -1, 1, 343deg) rotatex(27deg) scale(0.9);
  color: #ffcaf7;
  font-weight: bold;
  font-size: 1.2rem;
  transition: all 0.2s;
}

.button:before {
  content: "";
  width: 97%;
  position: absolute;
  height: 91%;
  top: 4.5%;
  z-index: -2;
  left: 1.3%;
  border-radius: 1.1rem;
  box-shadow: inset 0 -5px 5px -1px #7926bc;
  background: linear-gradient(to bottom, #d591ff, #7f2dc2);
}

.button:hover {
  transition: all 0.2s;
  transform: scale(1.2);
  box-shadow: inset -0.5px -3px 3px 1px #7926bc;
}

.button:active {
  transition: all 0.1s;
  box-shadow: inset 2px 2px 3px #5a2982ba;
  transform: scale(1.15);
}

.icon {
  width: 1.8rem;
  height: 1.8rem;
}

.button span {
  background: -webkit-linear-gradient(#ffe4fe, #ffcaf7);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
}

