/* From Uiverse.io by Na3ar-17  - Tags: simple, icon, animation, button */
.button {
  font-family: Arial, Helvetica, sans-serif;
  font-size: 18px;
  cursor: pointer;
  transition: all 0.2s ease;
  display: flex;
  align-items: center;
  font-weight: 500;
  background-color: #268bff;
  color: white;
  border: none;
  gap: 2px;
  border-radius: 8px;
  padding: 4px 8px;
}

.button .icon {
  width: 20px;
}

.button:hover {
  background-color: #268bffaf;
}
.button:active {
  transform: translateY(2px);
}

