/* From Uiverse.io by Programming-School-Pro-Coding - Tags: button */
button {
  --c1: #f6d365;
  --c2: #fda085;
  padding: 15px 30px;
  text-align: center;
  text-transform: uppercase;
  transition: 0.5s;
  background: linear-gradient(90deg, var(--c1, #f6d365), var(--c2, #fda085) 51%, var(--c1, #f6d365)) var(--x, 0)/ 200%;
  color: white;
  border-radius: 20px;
  outline: none;
}

button:hover {
  --x: 100%;
}

