/* From Uiverse.io by RenouxM  - Tags: simple, 3d, button */
.button {
  box-sizing: border-box;
  cursor: pointer;
  padding: 0;
  display: flex;
  justify-content: center;
  align-items: center;
  margin: 50px auto;
  background-color: rgb(245, 134, 161);
  font-family: arial;
  font-size: 20px;
  font-weight: bold;
  border: none;
  width: 280px;
  height: 90px;
  border-radius: 12px;
}
.front {
  display: block;
  padding: 28px 80px;
  border-radius: 12px;
  font-size: 1.25rem;
  background: rgb(245, 74, 117);
  transform: translateY(-4px);
}
.button:focus:not(:focus-visible) {
  outline: none;
}

