  width: 100%;
  height: 100%;
  font-size: 32px;
  font-weight: 900;
  color: #ffffff33;
  transition: all ease-in-out 1s;
}

.button__text::before {
  content: '';
  position: absolute;
  z-index: 1;
  top: 0;
  left: 0;
