original.css:
/* From Uiverse.io by Spacious74  - Tags: flashy, social, button, btn, 3d button */
.button {
  cursor: pointer;
  border: solid 4px #161616;
  border-top: none;
  border-radius: 20px;
  position: relative;
  box-shadow: 0px 4px 10px #00000062, 0px 10px 40px -10px #000000a6,
    0px 12px 45px -15px #00000071;
  transition: all 0.3s ease;
}
.inner {
  padding: 12px 30px;
  font-size: 1.5rem;
  display: flex;
  align-items: center;
  gap: 12px;
  font-weight: 600;
  letter-spacing: 1px;
  border-bottom: solid 3px #374e72;
  border-radius: 16px;
  background: linear-gradient(180deg, #5771a5, #000);
  color: #fff;
  text-shadow: 1px 1px #000, 0 0 9px #fff;
}
.svgs {
  position: relative;
  margin-top: 9px;
  z-index: 10;
}
.svgs > * {
  filter: drop-shadow(0 0 6px #fff) drop-shadow(1px 1px 0px #000);
}
.svgs .svg-s {
  position: absolute;
  font-size: 0.8rem;
  left: 20px;
  top: -4px;
}
.button:active {
  box-shadow: none;
}

