/* From Uiverse.io by ZiyadOuamna  - Tags: button, active, like, dark, light, shadow, modern, click */
.button {
  cursor: pointer;
  padding: 10px 20px;
  border-radius: 15px;
  background-color: gray;
  font-weight: bolder;
  font-size: 25px;
  border: 2px solid black;
  transition: all 0.5s;
}
.button:hover {
  background-color: aqua;
  box-shadow: 0px 0px 15px aqua;

  transform: scale(0.9);
}

