/* From Uiverse.io by andrew-demchenk0 - Tags: animation, blue, button, message */
.button {
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 6px 12px;
  gap: 8px;
  height: 32px;
  width: 124px;
  border: none;
  background: #056bfae0;
  border-radius: 20px;
  cursor: pointer;
}

.lable {
  line-height: 20px;
  font-size: 17px;
  color: #fff;
  font-family: sans-serif;
  letter-spacing: 1px;
}

.button:hover {
  background: #056DFA;
}

.button:hover .svg-icon {
  animation: msg 2s linear infinite;
}

@keyframes msg {
  0% {
    transform: translate(0, 0) scale(1);
  }

  15% {
    transform: translateX(1px);
  }

  30% {
    transform: scale(1.1);
  }

  45% {
    transform: translateY(1px);
  }

  60% {
    transform: translateY(0px);
  }

  75% {
    transform: scale(1);
  }

  90% {
    transform: translateX(-px);
  }

  100% {
    transform: translate(0, 0) scale(1);
  }
}

