/* From Uiverse.io by e-coders - Tags: button */
button.button-3d {
 margin: 10px;
 border-right: 0;
 border-left: 0;
 border-top: 0;
 font-family: inherit;
 font-size: 24px;
 padding: 8px;
 color: rgba(255, 255, 255, 0.788);
}

button.button-3d:active {
 border-bottom: 0;
}

button.button-3d {
 cursor: pointer;
}

/* RED */
.button-3d-red {
 border-bottom: 3px solid rgb(117, 28, 28);
 background-color: rgb(248, 91, 91);
}

