/* From Uiverse.io by ercnersoy - Tags: button */
button {
  padding: 0 20px 0 10px;
  border-radius: 10px;
  box-shadow: 0px 0px 5px 7px #e7413373;
  background-color: #e74133;
  color: white;
  font-size: 17px;
  border: none;
  display: flex;
  align-items: center;
  transition: all .5s ease-in-out;
  letter-spacing: 2px;
}

button:hover {
  background-color: #f54d3e;
  transition: all .5s ease-in-out;
  box-shadow: 0px 0px 5px 3px #e7413373;
}

button::before {
  content: "";
  background-image: url("data:image/svg+xml;base64,");
  background-size: 100%;
  background-repeat: no-repeat;
  color: transparent;
  position: relative;
  width: 50px;
  height: 50px;
  display: block;
  margin-right: 5px;
  transition: all .9s ease-in-out;
}

button:hover:before {
  background-image: url("data:image/svg+xml;base64,");
  transition: all .9s ease-in-out;
  transform: rotate(-1turn);
}

