/* From Uiverse.io by ercnersoy - Tags: button */
button {
  background: #2389e9;
  padding: 30px;
  color: #FFFFFF;
  display: flex;
  justify-content: center;
  align-items: center;
  border: none;
  transition: all .5s ease-in-out;
}

button:hover {
  border-radius: 50%;
  transition: all .5s ease-in-out;
}

button:hover:before {
  margin-left: 0%;
  transform: rotate(24deg);
}

button::before {
  content: "";
  background-image: url("data:image/svg+xml;base64,");
  height: 50px;
  background-repeat: no-repeat;
  position: absolute;
  width: 50px;
  transition: all .9s ease-in-out;
  background-size: 100%;
}

