/* From Uiverse.io by ercnersoy - Tags: button */
button {
  padding: 5px;
  border-radius: 100%;
  box-shadow: 0px 0px 5px 7px #1475ec73;
  background-color: #1474ec;
  color: white;
  font-size: 17px;
  border: none;
  display: flex;
  align-items: center;
}

button::before {
  content: "fac";
  background-image: url("data:image/svg+xml;base64,");
  background-size: 100%;
  background-repeat: no-repeat;
  color: transparent;
  position: relative;
  width: 50px;
  height: 50px;
  display: block;
}

button:active {
  border-radius: 100%;
  box-shadow: inset 0px 0px 10px 0px rgb(240, 237, 237);
}

