original.css:
/* From Uiverse.io by mRcOol7  - Tags: button, ring, futuristic */
.ring-futuristic-button {
  width: 200px;
  height: 60px;
  background: linear-gradient(to bottom right, #ff7e5f, #feb47b);
  color: white;
  font-size: 16px;
  border: none;
  border-radius: 10px;
  cursor: pointer;
  outline: none;
  position: relative;
  overflow: hidden;
  display: flex;
  justify-content: center;
  align-items: center;
  font-family: "Segoe UI", Tahoma, Geneva, Verdana, sans-serif;
  letter-spacing: 1px;
  transition: transform 0.5s, background 0.5s, color 0.3s, box-shadow 0.3s;
  position: relative;
  z-index: 1;
  box-shadow: 0 5px 15px rgba(0, 0, 0, 0.2);
}

.ring-futuristic-button::before {
  content: "";
  position: absolute;
  width: 100%;
  height: 100%;
  border: 2px solid rgba(255, 255, 255, 0.5);
  border-radius: 50%;
  box-sizing: border-box;
  opacity: 0;
  transform: scale(0);
  transition: transform 0.5s, opacity 0.5s;
}

.ring-futuristic-button:hover {
  background: linear-gradient(to bottom right, #feb47b, #ff7e5f);
  color: #fff;
  transform: scale(1.05);
  box-shadow: 0 10px 20px rgba(0, 0, 0, 0.3);
}

.ring-futuristic-button:hover::before {
  opacity: 1;
  transform: scale(2);
}

.ring-futuristic-button:active {
  transform: scale(0.95);
  transition: transform 0.1s;
}

