original.css:
/* From Uiverse.io by mininaim - Tags: button */
.space-button {
  font-size: 1.2rem;
  font-weight: bold;
  text-transform: uppercase;
  border: none;
  border-radius: 5px;
  padding: 10px 20px;
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
}

.space-icon {
  margin-right: 10px;
  fill: currentColor;
}

/* Light Mode */
@media (prefers-color-scheme: light) {
  .space-button {
    background-color: #0074D9;
    color: #fff;
    box-shadow: 0px 0px 10px 0px #0074D9;
  }

  .space-button:hover {
    box-shadow: 0px 0px 20px 0px #fff;
  }

  .space-button:active {
    box-shadow: none;
    background-color: #001f3f;
  }
}

/* Dark Mode */
@media (prefers-color-scheme: dark) {
  .space-button {
    background-color: #f0f0f0;
    color: #000;
    box-shadow: 0px 0px 10px 0px #000;
  }

  .space-button:hover {
    box-shadow: 0px 0px 20px 0px #fff;
  }

  .space-button:active {
    box-shadow: none;
    background-color: #bbb;
  }
}

