/* From Uiverse.io by neerajbaniwal  - Tags: simple, button, 3d button */
.button {
  display: inline-flex;
  align-items: center;
  padding: 15px 40px;
  background-color: #fefefe;
  border: 1px solid #dcdcdc;
  border-radius: 12px;

  font-family: "Ubuntu", sans-serif;
  font-size: 16px;
  font-weight: bold;
  color: #333;
  cursor: pointer;
  text-decoration: none;
  transition: all 0.3s ease;
  position: relative;
  overflow: hidden;
  z-index: 0;
  outline: 2px solid #fafafa;
  box-shadow: 9px 9px 44px #cecece3a, -9px -9px 44px #d3d3d33d,
    inset 0 0 0 2px rgba(192, 192, 192, 0.25);
}

.button::before {
  content: "";
  position: absolute;
  top: 3px;
  left: 3px;
  right: 3px;
  bottom: 3px;
  border-radius: 30px;
  background: linear-gradient(
    0deg,
    #fbfbfb 0%,
    #fafafa 5%,
    #f7f7f7 72%,
    #f2f2f2 86%,
    #f0f0f0 100%
  );
  box-shadow: inset 0 2px 7px rgba(143, 143, 143, 0.16);
  transition: all 0.3s ease;
  z-index: -1;
  margin: 2px;
}

.button:focus {
  outline: 2px solid #fefefe;
  box-shadow: 9px 9px 44px #cecece3a, -9px -9px 44px #d3d3d33d,
    inset 0 0 0 2px rgba(192, 192, 192, 0.5);
}

.send-icon {
  margin-right: 5px;
  transition: transform 0.3s ease;
}

.button:hover span {
  transform: translateX(7px);
  transition: all 0.4s;
}

.button span {
  transform: rotate(-40deg);
  margin-right: 10px;
  transition: all 0.4s;
}

