/* From Uiverse.io by nishikalewis  - Tags: button */
button {
  background: none;
  color: cyan;
  border: 2px solid cyan;
  padding: 1em 2em;
  font-size: 1em;
  border-radius: 0.5em;
  cursor: pointer;
  transition: all 0.25s ease-in-out;
}

button:hover {
  border-color: magenta;
  color: white;
  box-shadow: 0 0.6em 0.6em -0.3em magenta;
  transform: translateY(-0.3em);
}

