  width: fit-content;
  padding: 12px 24px;
  border-radius: 100px;
  border: none;
  box-shadow: inset 0px 22px 20px 10px rgb(0, 0, 0);
  font-weight: 800;
  color: antiquewhite;
  letter-spacing: .1rem;
  transition: all 0.51s ease-in-out;
}

.button:hover {
  transform: translateY(5px);
  box-shadow: inset 1px -11px 25px 10px rgb(91, 88, 240);
}

