/* From Uiverse.io by rrr63 - Tags: button, hover, outlined, bootsrap button */
.button {
  font-size: 17px;
  padding: 15px 35px;
  border-radius: 5px;
  outline: 2px solid #2835ba;
  outline-offset: 0px;
  background: #2835ba;
  border: 0;
  font-weight: bolder;
  color: white;
  transition: all .1s ease-in-out;
  cursor: pointer;
}

.button:hover {
  outline-offset: 3px;
  outline: 3px solid #2835ba;
}

