/* From Uiverse.io by sherghan7 - Tags: button */
button {
  min-width: 120px;
  background: #000;
  color: #EEEEEE;
  height: 40px;
  margin: 10px;
  border: none;
  border-radius: 0 !important;
  font-size: 16px;
  font-weight: bold;
  cursor: pointer;
  outline: none;
  transition: all 0.3s ease-in-out;
}

button:hover {
  box-shadow: #EEEEEE 0px 0px 0px 11px, #161616 0px 0px 0px 10px;
  background: #000;
  color: #EEEEEE;
}

