/* From Uiverse.io by vinodjangid07  - Tags: tooltip, button, hover effect, fullscreen, full window */
.fullscreen-btn {
  width: 25px;
  height: 25px;
  border: none;
  display: flex;
  align-items: center;
  justify-content: center;
  background-color: transparent;
  cursor: pointer;
  transition: all 0.3s;
  position: relative;
  overflow: hidden;
}

.fullscreen-btn svg {
  height: 100%;
}
.fullscreen-btn:hover {
  width: 30px;
  height: 30px;
  overflow: visible;
}
.tooltip {
  position: absolute;
  top: -40px;
  background-color: rgba(0, 0, 0, 0.753);
  color: white;
  padding: 5px 10px;
  border-radius: 5px;
  font-size: 0.8em;
  transition: all 0.3s;
  opacity: 0;
}
.fullscreen-btn:hover .tooltip {
  transform: translateY(2.5px);
  opacity: 1;
}

