/* From Uiverse.io by vinodjangid07  - Tags: animation, black, button, code, hover effect */
.button {
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 5px;
  height: 45px;
  width: 45px;
  border: none;
  border-radius: 10px;
  background-color: black;
  cursor: pointer;
  transition: all 0.3s;
  overflow: hidden;
}
.bracket {
  font-size: 18px;
  transition: all 0.3s;
  color: rgb(182, 104, 255);
}
.text {
  font-size: 15px;
  width: 0;
  transform: scale(0);
  transition: all 0.3s;
  color: white;
}
.button:hover {
  width: 100px;
}
.button:hover .text {
  transform: scale(1);
  width: 40px;
}

