original.css:
/* From Uiverse.io by vinodjangid07  - Tags: icon, animation, button, custom, hover effect */
.palette-button {
  background-color: #fff;
  width: 170px;
  height: 55px;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 10px;
  font-size: 16px;
  font-weight: 600;
  border: none;
  border-radius: 10px;
  cursor: pointer;
  box-shadow: 0px 0px 10px rgb(196, 196, 196);
  transition: all 0.3s;
}
.palette {
  width: 30px;
  transition: all 0.3s;
}
.palette-button:hover .palette {
  transform: rotate(-45deg);
}
.palette path {
  fill: rgb(255, 163, 183);
  stroke-width: 0;
}
.drop1 {
  fill: rgb(115, 115, 255);
  stroke-width: 0;
}
.drop2 {
  fill: red;
  stroke-width: 0;
}
.drop3 {
  fill: yellow;
  stroke-width: 0;
}
.drop4 {
  fill: rgb(104, 255, 255);
  stroke-width: 0;
}
.palette-button:hover {
  transform: scale(1.05);
  background-color: rgb(176, 115, 255);
  color: white;
}

