/* From Uiverse.io by wizard503 - Tags: button */
button#space-btn {
  width: 200px;
  height: 60px;
  position: relative;
  border: none;
  border-radius: 30% 70% 70% 30% / 30% 30% 70% 70%;
  box-shadow: inset 0px 0px 21px #3b2ad5;
  background: #03001e;
  cursor: pointer;
}

button#space-btn:hover {
  background: linear-gradient(180deg, #3b2ad5 0%,#88007d 60%,#ff0055c4 100%);
  box-shadow: 0px 5px 10px #03001e,inset 0px 5px 10px #8611d4,inset 0 -5px 10px #ff0055a1;
}

.btn-container:hover {
  background: #03001e;
  box-shadow: 0px -10px 40px #9611d4,0px 5px 10px #00a2ffe3,inset 0px 10px 21px #3b2ad5;
}

.btn-container {
  display: flex;
  justify-content: center;
  align-items: center;
  border-radius: 70% 30% 30% 70% / 70% 70% 30% 30%;
  width: 205px;
  height: 65px;
  background: linear-gradient(0deg, #88007d 0%,#ff0055c4 100%);
}

button span {
  color: white;
  font-weight: bold;
  font-size: 20px;
}

button div.star {
  opacity: 0;
  position: absolute;
  border-radius: 100%;
  background-color: white;
  width: 1px;
  height: 1px;
}

button:hover .star {
  opacity: 1;
}

button .planet {
  margin-left: -2.5em;
  opacity: 0;
  width: 30px;
  height: 30px;
  border-radius: 50%;
  border: none;
  position: absolute;
  background: linear-gradient(rgb(255, 0, 0), transparent),linear-gradient(to top left, rgb(255, 0, 212), transparent), linear-gradient(to top right, rgb(251, 255, 0), transparent);
  background-blend-mode: screen;
  background-size: 100% 200%;
  filter: drop-shadow(0px 0px 4px rgb(82, 59, 16));
}

button:hover .planet {
  z-index: 1;
  opacity: 1;
  animation: orbita 6s reverse linear infinite;
}

.planet::after {
  content: '';
  width: 40px;
  height: 10px;
  border: 3px solid #813dff;
  filter: drop-shadow(0px 0px 1px #cf3333);
  border-width: 0px 3px 3px 3px;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%) rotate(15deg);
  border-radius: 50%;
}

.planet::before {
  position: absolute;
  color: rgb(255, 0, 0);
  visibility: hidden;
}

.moon {
  margin-left: -3rem;
  position: absolute;
  width: 30px;
  height: 30px;
  background-color: #fff;
  border-radius: 100%;
  box-shadow: 0px 0px 10px 5px rgb(146, 146, 140);
}

.moon::after {
  position: absolute;
  content: '';
  margin-top: 15%;
  box-shadow: 0px 0px 10px 3px #333;
  margin-left: -10%;
  border-radius: 100%;
  width: 3px;
  height: 3px;
  background-color: rgb(121, 121, 121);
}

.moon1::after {
  position: absolute;
  content: '';
  margin-top: 65%;
  box-shadow: 0px 0px 8px 3px #333;
  margin-left: -9px;
  border-radius: 100%;
  width: 2px;
  height: 2px;
  background-color: rgb(121, 121, 121);
}

.moon::before {
  position: absolute;
  content: '';
  margin-top: 30%;
  margin-left: -30%;
  border-radius: 100%;
  box-shadow: 0px 0px 8px 1px rgb(3, 3, 3);
  width: 5px;
  height: 5px;
  background-color: rgb(134, 133, 133);
}

.moon1::before {
  position: absolute;
  content: '';
  margin-top: 70%;
  margin-left: 4%;
  border-radius: 100%;
  box-shadow: -5px 1px 4px 3px rgb(134, 133, 133);
  width: 2px;
  height: 2px;
  background-color: rgb(134, 133, 133);
}

button span.moon,span.moon1 {
  opacity: 0;
}

button:hover span.moon,span.moon1 {
  z-index: -1;
  opacity: 1;
  animation: orbita 2.7s linear infinite;
}

@keyframes orbita {
  0% {
    transform: rotate(0deg);
    transform: translateX(90px);
  }

  100% {
    rotate: 360deg;
    transform: translateX(90px);
  }
}

