/* From Uiverse.io by 0xnihilism  - Tags: loading, loader, animated, light&dark, brutalism, orwellian */
.orwellian-container {
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100%;
  margin: 0;
  overflow: hidden;
  font-family: "Arial Black", "Helvetica Neue", Arial, sans-serif;
  font-weight: 900;
}
.orwellian-container .orwellian-loader {
  width: 280px;
  height: 280px;
  background-color: #000;
  border: 10px solid #333;
  position: relative;
  overflow: hidden;
  padding: 20px;
  box-sizing: border-box;
}

.orwellian-container .eye {
  width: 160px;
  height: 60px;
  background-color: #fff;
  border-radius: 50%;
  position: absolute;
  top: 30%;
  left: 50%;
  transform: translate(-50%, -60%);
  animation: pulse 2s infinite;
  overflow: hidden;
  z-index: 10;
}

.orwellian-container .pupil {
  width: 50px;
  height: 50px;
  background-color: #000;
  border-radius: 50%;
  position: absolute;
  top: 33%;
  left: 50%;
  transform: translate(-50%, -50%);
  animation: look-diagonal 4s infinite ease-in-out;
}

.orwellian-container .pupil::after {
  content: "";
  position: absolute;
  top: 50%;
  left: 50%;
  width: 30px;
  height: 30px;
  background: radial-gradient(circle, rgb(255, 0, 0) 0%, transparent 70%);
  border-radius: 50%;
  transform: translate(-50%, -50%);
  opacity: 0.7;
}

.orwellian-container .eyelid {
  position: absolute;
  width: 100%;
  height: 100%;
  background-color: #000;
  animation: blink 4s infinite;
}

.orwellian-container .spotlight {
  position: absolute;
  top: 30%;
  left: 50%;
  width: 47px;
  height: 200px;
  background: linear-gradient(to bottom, rgba(255, 0, 0, 0.8), transparent);
  filter: blur(8px);
  transform-origin: top;
  animation: spotlight-sweep-diagonal 4s infinite ease-in-out;
}

.orwellian-container .text {
  position: absolute;
  bottom: 20px;
  left: 10px;
  right: 10px;
  text-align: center;
  font-size: 20px;
  font-weight: bold;
  color: #f8d17c;
  text-transform: uppercase;
  letter-spacing: 1px;
  animation: glitch 1s infinite;
  z-index: 20;
}

.orwellian-container .scan-lines {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: linear-gradient(
    to bottom,
    transparent 50%,
    rgba(0, 0, 0, 0.3) 50%
  );
  background-size: 100% 4px;
  animation: scan 10s linear infinite;
  z-index: 30;
  opacity: 0.3;
  pointer-events: none;
}

.orwellian-container .tv-effect {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: linear-gradient(rgba(18, 16, 16, 0) 50%, rgba(0, 0, 0, 0.25) 50%),
    linear-gradient(
      90deg,
      rgba(255, 0, 0, 0.06),
      rgba(0, 255, 0, 0.02),
      rgba(0, 0, 255, 0.06)
    );
  background-size: 100% 2px, 3px 100%;
  animation: tv-flicker 0.15s infinite;
  z-index: 35;
  pointer-events: none;
  opacity: 0.5;
}

.orwellian-container .orwellian-loader::after {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: repeating-linear-gradient(
    0deg,
    rgba(255, 255, 255, 0.03),
    rgba(255, 255, 255, 0.03) 1px,
    transparent 1px,
    transparent 2px
  );
  pointer-events: none;
  z-index: 40;
}

.orwellian-container .orwellian-loader::before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: radial-gradient(
    circle at center,
    transparent 30%,
    rgba(0, 0, 0, 0.5) 90%
  );
  z-index: 35;
  pointer-events: none;
}

@keyframes look-diagonal {
  0%,
  100% {
    transform: translateX(-50%) rotate(0deg);
  }
  25% {
    transform: translateX(-40%) rotate(-15deg);
  }
  75% {
    transform: translateX(-60%) rotate(15deg);
  }
}

@keyframes spotlight-sweep-diagonal {
  0%,
  100% {
    transform: translateX(-50%) rotate(0deg);
  }
  25% {
    transform: translateX(-40%) rotate(-15deg);
  }
  75% {
    transform: translateX(-60%) rotate(15deg);
  }
}

@keyframes pulse {
  0%,
  100% {
    transform: translate(-50%, -60%) scale(1);
  }
  50% {
    transform: translate(-50%, -60%) scale(1.05);
  }
}

@keyframes glitch {
  0%,
  100% {
    transform: translate(0, 0);
    opacity: 1;
  }
  25% {
    transform: translate(-2px, 2px);
    opacity: 0.75;
  }
  50% {
    transform: translate(2px, -2px);
    opacity: 0.5;
  }
  75% {
    transform: translate(-2px, -2px);
    opacity: 0.75;
  }
}

@keyframes blink {
