  top: -20rpx;
  right: -20rpx;
  bottom: -20rpx;
  left: -20rpx;
  border-radius: 400rpx;
  /* #ifdef H5 */
  /* #ifdef H5 */
  box-shadow: -10rpx -10rpx 30rpx rgba(15, 180, 231, 0.3);
  /* #endif */
  /* #ifdef MP */
  border: 1rpx solid rgba(0,0,0,0.1);
  /* #endif */
  /* #endif */
}

/* Middle Ring */
.ringTwo {
  border: 2rpx solid #444;
  position: absolute;
  top: -50rpx;
  right: -50rpx;
  bottom: -50rpx;
  left: -50rpx;
  border-radius: 400rpx;
  z-index: -2;
  background: linear-gradient(to bottom, #161616 0%, #3d3d3d 100%);
  animation: rotateClockwise 4s infinite linear; /* Rotate clockwise */
}

/* Core */
.core {
  color: #ccc;
  background-color: #ccc;
  position: absolute;
  top: 10rpx;
  left: 10rpx;
  right: 10rpx;
  bottom: 10rpx;
  z-index: 2;
  border-radius: 220rpx;
  background: radial-gradient(ellipse at center, #575b60 1%, #000000 100%);
  /* #ifdef H5 */
  /* #ifdef H5 */
  box-shadow:
    -2rpx 0rpx 0rpx #666,
    0rpx -2rpx 0rpx #666;
  /* #endif */
  /* #ifdef MP */
  border: 1rpx solid rgba(0,0,0,0.1);
  /* #endif */
  /* #endif */
  animation: pulse 2s infinite ease-in-out; /* Pulse animation */
}

.core:hover {
  background: radial-gradient(ellipse at center, #373a3d 1%, #000000 100%);
}

.core p {
  text-align: center;
  margin-top: 40%;
  font-size: 36rpx;
  font-family: sans-serif;
  font-weight: bolder;
  /* #ifdef H5 */
  text-shadow: -2rpx -2rpx 0rpx #444;
  /* #endif */
}

/* Spin Animation */
.spin {
  background: radial-gradient(
    ellipse at 20% 20%,
    #91ffff 0%,
    #0fb4e7 24%,
    #000000 100%
  );
  border-radius: 220rpx;
  padding: 20rpx;
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  animation: rotateAntiClockwise 6s infinite linear; /* Rotate anticlockwise */
}

/* Animations */

/* Rotate clockwise */
@keyframes rotateClockwise {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
}

/* Rotate anticlockwise */
@keyframes rotateAntiClockwise {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(-360deg);
  }
}

/* Pulse animation for the core */
@keyframes pulse {
  0% {
    transform: scale(1);
    /* #ifdef H5 */
  /* #ifdef H5 */
  box-shadow: 0 0 10rpx #666;
  /* #endif */
  /* #ifdef MP */
  border: 1rpx solid rgba(0,0,0,0.1);
  /* #endif */
  /* #endif */
  }
  50% {
    transform: scale(1.1);
    /* #ifdef H5 */
  /* #ifdef H5 */
  box-shadow: 0 0 30rpx #0fb4e7;
  /* #endif */
  /* #ifdef MP */
  border: 1rpx solid rgba(0,0,0,0.1);
  /* #endif */
  /* #endif */
  }
  100% {
    transform: scale(1);
    /* #ifdef H5 */
  /* #ifdef H5 */
  box-shadow: 0 0 10rpx #666;
  /* #endif */
  /* #ifdef MP */
  border: 1rpx solid rgba(0,0,0,0.1);
  /* #endif */
  /* #endif */
  }
}
