/* From Uiverse.io by JkHuger - Tags: loader */
.piggy-wrapper {
  position: relative;
  width: 350px;
  height: 350px;
  display: block;
  margin: 0 auto;
  margin-top: 0px;
}

.piggy-wrap {
  position: absolute;
  top: 0;
  left: 0;
  z-index: 10;
  width: 100%;
  height: 100%;
  transform-origin: bottom center;
  animation: trembling 5s linear infinite;
}

@keyframes trembling {
  0% {
    transform: scale(1);
  }

  19% {
    transform: scale(1);
  }

  21% {
    transform: scale(0.99);
  }

  22% {
    transform: scale(1);
  }

  23% {
    transform: scale(0.99);
  }

  24% {
    transform: scale(1);
  }

  25% {
    transform: scale(0.99);
  }

  26% {
    transform: scale(1);
  }

  25% {
    transform: scale(0.99);
  }

  26% {
    transform: scale(1);
  }

  27% {
    transform: scale(0.99);
  }

  28% {
    transform: scale(1);
  }

  29% {
    transform: scale(0.99);
  }

  30% {
    transform: scale(1);
  }

  60% {
    transform: scale(0.95);
  }

  61% {
    transform: scale(1);
  }

  100% {
    transform: scale(1);
  }
}

.piggy {
  position: absolute;
  bottom: 40px;
  left: 50%;
  margin-left: -160px;
  width: 270px;
  height: 200px;
