  width: 0;
  height: 0;
  z-index: 8;
  display: block;
  border-bottom: 60px solid #d88fa0;
  border-left: 20px solid transparent;
  border-right: 20px solid transparent;
  border-radius: 4px;
}

.ear {
  position: absolute;
  top: -35px;
  left: 70px;
  width: 45px;
  height: 40px;
  display: block;
  border-top-right-radius: 60px;
  background-color: #d88fa0;
