/* From Uiverse.io by Praashoo7 - Tags: animation, loader */
.loader2 {
  display: flex;
  align-items: center;
}

.load21 {
  position: relative;
  content: "";
  height: 0.2em;
  width: 4em;
  border-radius: 5px;
  opacity: 0;
  background-color: white;
  box-shadow: 0 1px 7px rgba(0,0,0,0.3);
  animation: 2s flow_21 infinite;
  animation-delay: 0.2s;
}

