}

.load25 {
  position: absolute;
  content: "";
  height: 0.2em;
  width: 4em;
  opacity: 0;
  border-radius: 5px;
  background-color: white;
  animation: 2s flow_21 infinite;
  animation-delay: 1s;
}

@keyframes flow_21 {
  0% {
    transform: translateY(1em);
    opacity: 1;
  }

  50% {
    transform: translateY(-1em);
    opacity: 1;
  }

  100% {
    transform: translateY(1em);
    opacity: 1;
  }
}

