/* From Uiverse.io by Shoh2008 - Tags: loader */
.loader {
  width: 70px;
  height: 35px;
  position: relative;
  overflow: hidden;
}

.loader:before {
  content: "";
  width: 70px;
  height: 70px;
  position: absolute;
  left: 0;
  top: 0;
  border: 5px solid #0000;
  border-color: #fff #fff #0000 #0000;
  border-radius: 50%;
  box-sizing: border-box;
  animation: rotate_5131 3s ease-in-out infinite;
  transform: rotate(-200deg);
}

@keyframes rotate_5131 {
  0% {
    border-width: 10px;
  }

  25% {
    border-width: 3px;
  }

  50% {
    transform: rotate(115deg);
    border-width: 10px;
  }

  75% {
    border-width: 3px;
  }

  100% {
    border-width: 10px;
  }
}

