/* From Uiverse.io by Tsiangana  - Tags: animation, loader, loading animation, anime */
.loader {
  width: 200px;
  height: 200px;
  display: flex;
  align-items: center;
  justify-content: center;
  animation: float 2s ease-in-out infinite;
}

@keyframes float {
  0%,
  100% {
    transform: translateY(0);
  }
  50% {
    transform: translateY(-12px);
  }
}

.loader:before {
  content: "";
  width: 10px;
  height: 7px;
  border-radius: 50%;
  background-color: rgba(0, 0, 0, 0.9);
  position: absolute;
  bottom: -9px;
  animation: shadow046 2s alternate infinite ease;
}
@keyframes shadow046 {
  0% {
    transform: scaleX(1);
  }
  40% {
    transform: scaleX(15);
    opacity: 0.7;
  }
}

.circle {
  width: 140px;
  height: 140px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  position: absolute;
}
.loader .p1 {
  height: 20px;
  width: 7px;
  background-color: #ffe71c;
  position: absolute;
  margin-top: -118px;
