/* From Uiverse.io by Tsiangana  - Tags: animation, loader, animated, creative, loading animation */
.light {
  position: absolute;
  left: 22px;
  fill: rgba(37, 37, 37, 0.774);
}

.loader {
  width: 80px;
  height: 40px;
  border: 2px solid #22f49e;
  border-right-color: transparent;
  padding: 3px;
  background: repeating-linear-gradient(90deg, #22f49e 0 10px, #0000 0 15px)
    left/0% 100% no-repeat content-box content-box;
  position: relative;
  animation: p5 2s infinite steps(6);
}
.loader::before {
  content: "";
  position: absolute;
  top: -2px;
  bottom: -2px;
  left: 100%;
  width: 10px;
  background: linear-gradient(
        #0000 calc(50% - 7px),
        #22f49e 0 calc(50% - 5px),
        #0000 0 calc(50% + 5px),
        #22f49e 0 calc(50% + 7px),
        #0000 0
      )
      left / 100% 100%,
    linear-gradient(#22f49e calc(50% - 5px), #0000 0 calc(50% + 5px), #22f49e 0)
      left / 2px 100%,
    linear-gradient(#0000 calc(50% - 5px), #22f49e 0 calc(50% + 5px), #0000 0)
      right/2px 100%;
  background-repeat: no-repeat;
}
@keyframes p5 {
  100% {
    background-size: 120% 100%;
  }
}

