.wrapper {
  width: 300px;
  height: 300px;
	/*border:1px solid red;*/
  margin: 0 auto;
  margin-top: 5%;
  position: relative;
}

.hoop {
  width: 80px;
  height: 80px;
  border-radius: 50%;
  background: transparent;
  box-shadow: 0 0 0 6px #96281B, 0 0 0 26px #D64541;
  position: absolute;
  left: 100px;
  top: 100px;
  z-index: 5;
}

.sphere1 {
  width: 50px;
  height: 50px;
  border-radius: 50%;
  background: #22A7F0;
  position: absolute;
  left: 116px;
  top: 116px;
  box-shadow: -7px -7px 0 #1E8BC3 inset;
  -webkit-animation: sphere1Move 1.2s linear infinite;
  animation: sphere1Move 1.2s linear infinite;
}

.sphere2 {
  width: 50px;
  height: 50px;
  border-radius: 50%;
  background: #22A7F0;
  position: absolute;
  left: 116px;
  top: 116px;
  box-shadow: -7px -7px 0 #1E8BC3 inset;
  -webkit-animation: sphere2Move 1.2s linear 0.6s infinite;
  animation: sphere2Move 1.2s linear 0.6s infinite;
}

