/* From Uiverse.io by davidhbt - Tags: loader */
@keyframes bounce {
  0%, 80%, 100% {
    transform: scale(0);
    opacity: 0;
  }

  40% {
    transform: scale(1);
    opacity: 1;
  }
}

.loader {
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
  width: 100px;
  height: 100px;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
}

.circle {
  width: 20px;
  height: 20px;
  background-color: rgb(83, 82, 82);
  border-radius: 50%;
  margin: 2px;
  animation: bounce 1.2s infinite ease-in-out;
}

.circle:nth-child(2) {
  animation-delay: -0.1s;
}

.circle:nth-child(3) {
  animation-delay: -0.2s;
}

.circle:nth-child(4) {
  animation-delay: -0.3s;
}

.circle:nth-child(5) {
  animation-delay: -0.4s;
}

.circle:nth-child(6) {
  animation-delay: -0.5s;
}

.circle:nth-child(7) {
  animation-delay: -0.6s;
}

.circle:nth-child(8) {
  animation-delay: -0.7s;
}

.circle:nth-child(9) {
  animation-delay: -0.8s;
}

.circle:nth-child(10) {
  animation-delay: -0.9s;
}

