/* From Uiverse.io by elijahgummer  - Tags: simple, white, loader, dots, circle, svg, minimal, dashofset */
.loader {
  width: 48px;
  height: 48px;
  transform: rotate(0deg);
  animation: spin 2.4s linear infinite;
}

.loader line {
  stroke: #ffffff; /* White color for better visibility */
  stroke-width: 4;
  stroke-linecap: round;
  opacity: 0.8;
  stroke-dasharray: 10;
  stroke-dashoffset: 10;
  animation: dash 2.4s ease-in-out infinite;
}

@keyframes dash {
  0% {
    stroke-dashoffset: 10;
    opacity: 0.8;
  }
  50% {
    stroke-dashoffset: 0;
    opacity: 0.4;
  }
  100% {
    stroke-dashoffset: 10;
    opacity: 0.8;
  }
}

@keyframes spin {
  0% {
    transform: rotate(0deg);
  }
  100% {
    transform: rotate(360deg);
  }
}

