/* From Uiverse.io by mrhyddenn - Tags: loader, spinner */
.loader {
  width: 40px;
  height: 40px;
  border-radius: 50%;
  border: 3px solid rgb(255,255,255,.2);
  border-top-color: transparent;
  animation: rot1 1.2s linear infinite;
}

@keyframes rot1 {
  to {
    transform: rotate(360deg);
  }
}

