.container {
  background-color: #355463;
  height: 100%;
  width: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
}

.road {
  position: relative;
  width: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
  transform-style: preserve-3d;
  transform: rotate(-15deg);
}

.road::before {
  content: "";
  position: absolute;
  width: 100%;
  height: 4px;
  background: linear-gradient(
    90deg,
    #fff,
    #fff 50%,
    transparent 50%,
    transparent 100%
  );
  background-size: 50px;
  animation: animate_road 0.07s linear infinite;
}

@keyframes animate_road {
  0% {
    background-position: 50px 0;
  }
  100% {
    background-position: 0px 0;
  }
}

.police {
  position: absolute;
  left: -100px;
  width: 200px;
  height: 80px;
  background-color: #3f4c54;
  border-radius: 10px;
  box-shadow: -10px 10px rgba(0, 0, 0, 0.05);
  transform-style: preserve-3d;
  perspective: 800px;
  z-index: 10;
  animation: animate_police_car 5s linear infinite;
}

.police:nth-child(2) {
  left: 200px;
  animation-delay: -2.5s;
}

@keyframes animate_police_car {
  0% {
    transform: translateX(20px) translateY(-50px);
  }
  25% {
    transform: translateX(-10px) translateY(0);
  }
  50% {
    transform: translateX(20px) translateY(50px);
  }
  75% {
    transform: translateX(-10px) translateY(0px);
  }
  100% {
    transform: translateX(20px) translateY(-50px);
  }
}

.police::before {
  content: "";
  position: absolute;
  top: 12px;
  left: 3px;
  width: 5px;
  height: 15px;
  border-radius: 2px;
  box-shadow: 0 42px #fc3c25;
  background-color: #fc3c25;
}

.police::after {
  content: "";
  position: absolute;
  top: 12px;
  right: 2px;
  width: 6px;
  height: 16px;
  border-radius: 2px;
  box-shadow: 0 44px #fff;
  background-color: #fff;
}

.police span {
  position: absolute;
  inset: 5px 10px;
  background-color: #475b66;
  border-radius: 10px;
  overflow: hidden;
}

.police span::before {
  content: "911";
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%) rotate(90deg);
  color: #333;
  font-size: 1.15em;
  font-weight: 1000;
  letter-spacing: 0.1em;
  z-index: 10;
}

.police span::after {
  content: "";
  position: absolute;
  top: 35%;
  left: 60%;
  transform: translate(-50%, 50%);
  z-index: 100;
  width: 6px;
  height: 10px;
  background: #29282b;
  box-shadow: 0 -10px #1f5fff, 0 10px #df1b25;
}

.police span b {
  position: absolute;
  inset: 0 30px;
  background-color: #344650;
  border-radius: 10px;
}

.police span b::before {
  content: "";
  position: absolute;
  inset: 6px 25px;
  background: #ebebeb;
  border-radius: 5px;
}

.police span b::after {
  content: "";
  position: absolute;
  inset: 0 58px;
  background: #ebebeb;
}

.police span i {
  position: absolute;
  inset: 0;
}

.police span i::before {
  content: "";
  position: absolute;
  top: 7px;
  left: 20px;
  width: 40px;
  height: 5px;
  background-color: #ebebeb;
  transform-origin: right;
  transform: rotate(15deg);
  box-shadow: 100px 34px #ebebeb;
}

.police span i::after {
  content: "";
  position: absolute;
  bottom: 7px;
  left: 20px;
  width: 40px;
  height: 5px;
  background-color: #ebebeb;
  transform-origin: right;
  transform: rotate(-15deg);
  box-shadow: 100px -34px #ebebeb;
}

.light_beam {
  position: absolute;
  top: 5px;
  right: -2px;
  width: 10px;
  height: 70px;
  background-color: #0002;
  border-radius: 15px;
  transform-style: preserve-3d;
}

.light_beam::before {
  content: "";
  position: absolute;
  top: 5px;
  left: 0;
  width: 150px;
  height: 16px;
  background: linear-gradient(90deg, #fff6, transparent);
  transform-origin: left;
  transform: perspective(500px) rotateY(-50degkk);
}

.light_beam::after {
  content: "";
  position: absolute;
  bottom: 5px;
  left: 0;
  width: 150px;
  height: 16px;
  background: linear-gradient(90deg, #fff6, transparent);
  transform-origin: left;
  transform: perspective(500px) rotateY(-50deg);
}

.police p {
  position: absolute;
  top: 50%;
  left: -5px;
  color: #fff;
  text-transform: uppercase;
  z-index: 10;
  transform: translateY(-50%) rotate(90deg) scale(0.75);
}

.police article {
  position: absolute;
