/* From Uiverse.io by omriluz - Source: online tutorials - https://www.youtube.com/watch?v=n6ojK2HmROE&list=PL5e68lK9hEzeeXtsQCQYd9SAzj6u6wZpi&index=153&ab_channel=OnlineTutorials - Tags: flashy, animation, loader, colorful, multicolor */
.container {
  position: relative;
  width: 100%;
  height: 200px;
  display: flex;
  align-items: center;
  justify-content: center;
  -webkit-box-reflect: below 0 linear-gradient(transparent, transparent, #0005);
}

.container .loader {
  position: absolute;
  width: 200px;
  height: 200px;
  border-radius: 50%;
  animation: animate 2s linear infinite;
}

.container .loader:nth-child(2),
.container .loader:nth-child(4) {
  animation-delay: -1s;
}

@keyframes animate {
  0% {
    transform: rotate(0deg);
    filter: hue-rotate(360deg);
  }

  100% {
    transform: rotate(360deg);
    filter: hue-rotate(0deg);
  }
}

.container .loader:nth-child(1)::before,
.container .loader:nth-child(2)::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  width: 50%;
  height: 100%;
  background: linear-gradient(to top, transparent, rgba(0,255,249, 0.4));
  background-size: 100px 180px;
  background-repeat: no-repeat;
  border-top-left-radius: 100px;
  border-bottom-left-radius: 100px;
}

.container .loader i {
  position: absolute;
  top: 0;
  left: 50%;
  transform: translateX(-50%);
  width: 20px;
  height: 20px;
  background: #00fff9;
  border-radius: 50%;
  z-index: 100;
  box-shadow: 0 0 10px #00fff9,
    0 0 30px #00fff9,
    0 0 40px #00fff9,
    0 0 50px #00fff9,
    0 0 60px #00fff9,
    0 0 70px #00fff9,
    0 0 80px #00fff9,
    0 0 90px #00fff9,
    0 0 100px #00fff9;
}

.container .loader span {
  position: absolute;
  inset: 20px;
  background: #e8e8e8;
  border-radius: 50%;
  z-index: 1;
}

