/* From Uiverse.io by polidahiya  - Tags: simple, loading, loader, download, downloading */
.loadingtext p {
  color: white;
  position: relative;
}

.loadingtext p::after {
  position: absolute;
  animation: b 3s infinite linear;
  color: white;
  content: "";
  left: 105%;
}

@keyframes b {
  0% {
    content: "";
  }

  10% {
    content: ".";
  }

  40% {
    content: "..";
  }

  70% {
    content: "...";
  }

  100% {
    content: "";
  }
}

