        #f92033 0 32px,
        #0000 0
      )
      bottom 20px left 50%,
    linear-gradient(
        90deg,
        #0000 8px,
        #643700 0 10px,
        #fdc98d 0 12px,
        #643700 0 16px,
        #fdc98d 0 22px,
        #000 0 24px,
        #fdc98d 0 30px,
        #f92033 0 32px,
        #0000 0
      )
      bottom 18px left 50%,
    linear-gradient(
        90deg,
        #0000 8px,
        #643700 0 12px,
        #fdc98d 0 20px,
        #000 0 28px,
        #f92033 0 30px,
        #0000 0
      )
      bottom 16px left 50%,
    linear-gradient(90deg, #0000 12px, #fdc98d 0 26px, #f92033 0 30px, #0000 0)
      bottom 14px left 50%,
    linear-gradient(
        90deg,
        #fdc98d 6px,
        #f92033 0 14px,
        #222a87 0 16px,
        #f92033 0 22px,
        #222a87 0 24px,
        #f92033 0 28px,
        #0000 0 32px,
        #643700 0
      )
      bottom 12px left 50%,
    linear-gradient(
        90deg,
        #fdc98d 6px,
        #f92033 0 16px,
        #222a87 0 18px,
        #f92033 0 24px,
        #f92033 0 26px,
        #0000 0 30px,
        #643700 0
      )
      bottom 10px left 50%,
    linear-gradient(
        90deg,
        #0000 10px,
        #f92033 0 16px,
        #222a87 0 24px,
        #feee49 0 26px,
        #222a87 0 30px,
        #643700 0
      )
      bottom 8px left 50%,
    linear-gradient(
        90deg,
        #0000 12px,
        #222a87 0 18px,
        #feee49 0 20px,
        #222a87 0 30px,
        #643700 0
      )
      bottom 6px left 50%,
    linear-gradient(90deg, #0000 8px, #643700 0 12px, #222a87 0 30px, #643700 0)
      bottom 4px left 50%,
    linear-gradient(90deg, #0000 6px, #643700 0 14px, #222a87 0 26px, #0000 0)
      bottom 2px left 50%,
    linear-gradient(90deg, #0000 6px, #643700 0 10px, #0000 0) bottom 0px left
      50%;
  background-size: 34px 2px;
  background-repeat: no-repeat;
  animation: inherit;
  animation-name: l10-1;
}
@keyframes l10-0 {
  0%,
  30% {
    background-position: 0 0px;
  }
  50%,
  100% {
    background-position: 0 -0.1px;
  }
}
@keyframes l10-1 {
  50%,
  100% {
    top: 109.5%;
  }
}
@keyframes l10-2 {
  0%,
  30% {
    transform: translateY(0);
  }
  80%,
  100% {
    transform: translateY(-260%);
  }
}

