/* From Uiverse.io by bociKond  - Website: https://codepen.io/milanraring/pen/JjoamZx - Name: Milan Raring - Tags: simple, tooltip, social, notification */
.tooltip-container {
  --white: #f2f2f2;
  --background: #1f8392;
  position: relative;
  background: var(--background);
  cursor: pointer;
  transition: all 0.2s;
  font-size: 2rem;
  font-weight: 900;
  color: var(--white);
  width: 5rem;
  aspect-ratio: 1/1;
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: 50%;
}

.tooltip {
  position: absolute;
  top: 0;
  left: 50%;
  transform: translateX(-50%);
  padding: 0.3em 0.6em;
  opacity: 0;
  pointer-events: none;
  background: var(--background);
  transition: all 300ms;
  fill: white;

  display: flex;
  align-items: center;
  gap: 0.3rem;
  border-radius: 30rem;
}
.tooltip .svg {
  height: 3rem;
  aspect-ratio: 1/1;
  cursor: pointer;
}
.tooltip .svg:nth-child(2) {
  width: 4rem;
}
.tooltip::before {
  position: absolute;
  content: "";
  height: 0.6em;
  width: 0.6em;
  bottom: -0.2em;
  left: 50%;
  transform: translate(-50%) rotate(45deg);
  background: var(--background);
}
.tooltip-container .text {
  transition: all 600ms;
}
.tooltip-container:hover .text {
  transform: rotate(225deg);
}
.tooltip-container:hover .tooltip {
  top: -100%;
  opacity: 1;
  visibility: visible;
  pointer-events: auto;
}

